example.screen:
import dabo
dabo.ui.loadUI("wx")
class MyCheckBox(dabo.ui.dCheckBox): def afterInit(self): MyCheckBox.doDefault() self.Caption = "Test Checkbox" self.bindEvent(dabo.dEvents.Hit, self.onHit)
def onHit(self, evt): print "Checkbox hit!"
if __name__ == "__main__": app = dabo.dApp() app.setup() app.MainForm.addObject(MyCheckBox, "chkTest") app.start()
